// 앨범 페이지 컴포넌트
import React from "react";

const Album = () => {
    return (
        <main className="contents_wrap">
          <h2>VIDEO</h2>
          <div className="contents_inner">
            <section id="main_mv">
              <iframe src="" title="" style={{ opacity: 0, transition: 'opacity 1s' }}></iframe>
            </section>
            <section id="sub_mv">
              <Swiper className="mv_wrap"
                slidesPerView={3}
                spaceBetween={20}
                navigation={true}
                modules={[Navigation]}>
                {vdata.map((x, i) => (
                  <SwiperSlide key={i} className="swiper-slide" onClick={()=>showVid(x.vsrc, x.txt)}>
                    <div className="mv_img">
                      <img src={x.isrc} />
                    </div>
                    <div className="mv_date">
                      <p>{x.txt}</p>
                      <p>{x.date}</p>
                    </div>
                  </SwiperSlide>
                ))}
              </Swiper>
            </section>
          </div>
        </main>
      );
}; // Album // 

export default Album;
