// 배너 컴포넌트 - Ban.js
import React from "react";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
// 애니메이션 라이브러리
import gsap from "gsap";
import { ReactSVG } from "react-svg";
import { ScrollTrigger } from "gsap/ScrollTrigger";
// 스와이퍼 컴포넌트
import { Swiper, SwiperSlide } from "swiper/react";
// 스와이퍼 네비게이션 컴포넌트
import { Navigation } from "swiper";
// 제이쿼리
import $ from "jquery";

// 배너CSS
import "../scss/ban.css";
// // 미디어쿼리CSS
// import "../scss/media.css";

// Import Swiper styles
import "swiper/css";
import "swiper/css/navigation";

// 배너 데이터
import ban_data from "../data/ban";

// 메인배너 출력용 컴포넌트
function Main_Ban(props) {
  const sel_data = ban_data[props.cat];

  return (
    <section className="banbx">
      <div className="bancont">
        <ul className="album_wrap">
          {sel_data.map((x, i) => (
            <li className="album_set" key={i}>
              <article className={"album_cover" + (i + 1)}>
                <div className="album_cover_inner">
                  <div>
                    <div className="original_author">
                      <span>{x.altop_txt.split("^")[0]}</span>
                      <span>/</span>
                      <span>{x.altop_txt.split("^")[1]}</span>
                    </div>
                    <em>{x.mtit}</em>
                  </div>
                  <div className="release_date">
                    <div className="date">
                      <span>{x.albot_txt.split("^")[0]}</span>
                      <span>{x.albot_txt.split("^")[1]}</span>
                    </div>
                    <div className="singer">(G)I-DLe</div>
                  </div>
                </div>
              </article>
              <aside className="album_cd1">
                <a href="#">
                  <img src={x.isrc} alt="앨범이미지1" />
                </a>
                <div className="cd_center_circle"></div>
              </aside>
            </li>
          ))}
        </ul>
      </div>
    </section>
  );
}

// 프로필배너 출력용 컴포넌트
function Profile_Ban(props) {
  const sel_data = ban_data[props.cat];
  const [imgclk, setImgClk] = useState(0);
  const [isLoading, setIsLoading] = useState(true);

  $(() => {
    // 멤버 프로필 리스트 클릭시 큰이미지박스 보이기
    const tgImg = $(".profile_img > li");
    tgImg.click(function () {
      // 1. 클릭된 이미지 경로 읽어오기
      let isrc = $(this).find("img").attr("src");

      // 2. 클릭된 이미지 경로를 큰 이미지에 src로 넣기
      $(".gimg > img").attr("src", isrc);

      // 3. 큰이미지박스 보이기
      $("#imbx").css({ display: "block" }).find(".imgbx").eq($(this).index()).css({ display: "table" }).siblings().css({ display: "none" });
    });

    // 4. 닫기버튼 클릭시 큰이미지박스 숨기기
    $(".close_btn").click(function () {
      $(this).parent().css({ display: "none" });
    });

    useEffect(() => {
      const SVG = () => {
        let bubblez = SVG("#maskBubblez");
        let numOfBubblez = 20;

        let circles = [{ x: 0, y: 0, radius: 0 }];

        for (let i = 0; i < numOfBubblez; i++) {
          function drawCircleWithoutOverlap() {
            let newX = gsap.utils.random(0, 100);
            let newY = gsap.utils.random(0, 100);
            let newRadius = gsap.utils.random(4, 20);
            let newCircle = { x: newX, y: newY, radius: newRadius };
            let isOverlapping = false;

            circles.forEach((circle, i) => {
              let deltaX = newCircle.x - circles[i].x;
              let deltaY = newCircle.y - circles[i].y;
              let dist = Math.hypot(deltaX, deltaY);
              let radiiiis = circles[i].radius + newCircle.radius;

              if (dist < radiiiis) {
                isOverlapping = true;
              }
            });

            if (isOverlapping) {
              drawCircleWithoutOverlap();
            } else {
              bubblez
                .circle(newCircle.radius)
                .x(newCircle.x)
                .y(newCircle.y)
                .fill("#fff")
                .opacity(newCircle.radius / 20);

              circles.push(newCircle);
            }
          }

          drawCircleWithoutOverlap();
        }

        gsap.registerPlugin(ScrollTrigger);

        gsap.to("circle", {
          y: () => 1 - gsap.utils.random(0.1, 0.4) * ScrollTrigger.maxScroll(window),
          ease: "none",
          scrollTrigger: {
            start: 0,
            end: "max",
            invalidateOnRefresh: true,
            scrub: 2,
          },
        });

        gsap.to("#text", {
          attr: {
            startOffset: -2500,
          },
          scrollTrigger: {
            start: 0,
            end: "20%",
            scrub: 2,
          },
        });
      };
    });
  });

  return (
    <>
      {sel_data.map((x, i) => (
        <main key={i} id="kv_wrap">
          {/* 1. 전체멤버 프로필 */}
          <section className="kv">
            <div>
              <img src={x.isrc} alt="여자아이들 이미지" />
            </div>
          </section>

          <div className="intro_profile">
            {/* 2. 그룹 소개 */}
            <section className="group_intro">
              <h2 className="conttents_tit">{x.mtit}</h2>
              <p className="contents-desc">{x.stit}</p>
            </section>

            {/* 스크롤시 등장하는 웨이브 텍스트 */}
            {isLoading ? (
              <div>Loading...</div>
            ) : (
              <>
                <svg id="bubblezSVG" viewBox="0 0 100 100" preserveAspectRatio="xMidYMid slice">
                  <mask id="maskBubblez">{/* Mask 내용 */}</mask>
                  <image mask="url(#maskBubblez)" preserveAspectRatio="xMidYMid slice" href="https://assets.codepen.io/756881/mesh-gradient+%283%29.png" height="150" width="100" />
                </svg>
                <svg id="textSVG" viewBox="0 0 1000 200">
                  <path fill="none" id="curve" d="M0,100S269.931,186.612,520,100C770.069,13.388,1000,100,1000,100" />
                  <text x="-50" fontSize="45">
                    <textPath href="#curve" startOffset="1200">
                      Wobbly text and gradient bubbles....
                    </textPath>
                  </text>
                </svg>
              </>
            )}

            {/* 3. 개별멤버 프로필 */}
            <section className="members_intro">
              <div className="members_inner">
                <div className="intro_title">
                  <h2>MEMBER</h2>
                </div>
                <ul className="profile_img">
                  {x.sub.map((v, n) => (
                    <li key={n}>
                      <div>
                        <img src={v.img} alt={v.name + "의 프로필"} />
                      </div>
                    </li>
                  ))}
                </ul>

                {/* 4. 큰이미지 박스 */}
                <section id="imbx">
                  {x.sub.map((item, i) => (
                    <div className="imgbx" key={i}>
                      {/* 큰 이미지 */}
                      <div className="gimg">
                        <img src="" alt="큰이미지" />
                      </div>
                      <dl className="gimgDetail">
                        <dt>{item.name}</dt>
                        <dd>{item.birth}</dd>
                      </dl>
                      <button type="button" className="close_btn" title="팝업 닫기"></button>
                    </div>
                  ))}
                </section>
              </div>
            </section>
          </div>
        </main>
      ))}
    </>
  );
}

// 비디오배너 출력용 컴포넌트
function Video_Ban(props) {
  // 데이터 셋팅
  const vdata = ban_data[props.cat];

  useEffect(() => {
    // 컴포넌트가 마운트될 때 첫 번째 li 클릭
    const firstLi = document.querySelector(".swiper-slide");
    if (firstLi) {
      firstLi.click();
    }

    // 두번째 인자가 빈 배열 []인 경우, 컴포넌트가 처음 마운트될 때만 실행됨.
  }, []);

  // 비디오 보이기 함수
  const showVid = (src, tit) => {
    let ifr = $("#main_mv iframe");
    // 1. 아이프레임 src넣기
    ifr.attr("src", src + "?autoplay=1");
    // 2. 아이프레임 title넣기
    ifr.attr("title", tit);
    ifr.css("opacity", 1);
  }; // Showvid //

  return (
    <main className="contents_wrap">
      <h2>VIDEO</h2>
      <div className="contents_inner">
        <section id="main_mv">
          <iframe src="" title="" style={{ opacity: 0, transition: "opacity 1s" }}></iframe>
        </section>
        <section id="sub_mv">
          <Swiper className="mv_wrap" slidesPerView={3} spaceBetween={20} navigation={true} modules={[Navigation]}>
            {vdata.map((x, i) => (
              <SwiperSlide key={i} className="swiper-slide" onClick={() => showVid(x.vsrc, x.txt)}>
                <div className="mv_img">
                  <img src={x.isrc} />
                </div>
                <div className="mv_date">
                  <p>{x.txt}</p>
                  <p>{x.date}</p>
                </div>
              </SwiperSlide>
            ))}
          </Swiper>
        </section>
      </div>
    </main>
  );
}

// 앨범 출력용 컴포넌트
function Album_Ban() {
  const sel_data = ban_data.main;

  return (
    <main className="contents_wrap">
      <h2>DISCOGRAPHY</h2>
      <div className="contents_inner">
        <div className="album_list">
          <ul>
            {sel_data.map((x, i) => (
              <li key={i}>
                <Link to={`/album/${i}`}>
                  <img src={x.isrc} alt={"여자아이들의 " + x.mtit + " 앨범"} />
                  <div className="albumInfo_box">
                    <div className="txt_align">
                      <h5>{x.altop_txt.split("^")[1]}</h5>
                      <h2>{x.mtit.replace(/-/g, "")}</h2>
                    </div>
                  </div>
                </Link>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </main>
  );
}

// 갤러리1 컴포넌트
function Gal1_comp(props) {
  const galVal = props.cat[0];

  return (
    <>
      <h3>Album Title : {galVal.galtit}</h3>
      <div className="gal1_cont">
        {/* 앨범명 */}
        <div className="gal_img_wrap">
          <div className="gal_img_inner">
            <ul>
              {galVal.galisrc.map((x, i) => (
                <li className={"gal1_img" + (i + 1)} key={i}>
                  <img src={`./images/gallery/${galVal.galtit}/${x}.jpg`} alt={"여자아이들의 " + galVal.galtit + " 앨범이미지"} />
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </>
  );
}

// // 갤러리2 컴포넌트
// function Gal2_comp

// 갤러리 출력용 컴포넌트
function Gallery_Ban() {
  const sel_data = ban_data.gallery;
  let prevIndex = 1;

  const firstSpan = $(".gallery_dot1");

  const [handleClick, setHandleClick] = useState(false);

  const handleNextClick = () => {
    setHandleClick((prevIndex) => (prevIndex + 1) % sel_data.length); // 다음 갤러리 인덱스로 변경
  };

  const handlePrevClick = () => {
    setHandleClick((prevIndex) => (prevIndex - 1 + sel_data.length) % sel_data.length); // 이전 갤러리 인덱스로 변경
  };

  return (
    <>
      <main className="contents_wrap">
        <h2>Gallery</h2>
        {/* 컴포넌트 출력 부분 */}
        <Gal1_comp cat={sel_data} />
      </main>
      <nav className="go_lft_rgt_btn">
        <div className="lft_rgt_btn_wrap">
          <div id="go_prev_btn" onClick={handlePrevClick}>
            <svg viewBox="0 0 6 10" xmlns="http://www.w3.org/2000/svg" fill="none">
              <path d="m5.216 9.4-4.2-4.2 4.2-4.2" stroke="#000" strokeWidth=".7"></path>
            </svg>
          </div>
          <div id="go_btn_dots">
            {sel_data.map((_, index) => (
              <span key={index} className={`gallery_dot${index + 1} ${index === handleClick ? "active" : ""}`} style={{ fontWeight: index === handleClick ? "700" : "400" }}>
                I
              </span>
            ))}
          </div>
          <div id="go_next_btn" onClick={handleNextClick}>
            <svg viewBox="0 0 6 10" xmlns="http://www.w3.org/2000/svg" fill="none">
              <path d="m5.216 9.4-4.2-4.2 4.2-4.2" stroke="#000" strokeWidth=".7"></path>
            </svg>
          </div>
        </div>
      </nav>
    </>
  );
}

export { Main_Ban, Profile_Ban, Video_Ban, Album_Ban, Gallery_Ban };
