// 플레이어 컴포넌트 - Player.js
import React, { useRef, useState, useEffect } from "react";
// 오디오 재생/컨트롤 라이브러리
import { Howl } from "howler";
// 플레이어CSS
import "../scss/player.css";
// 배너 데이터
import ban_data from "../data/ban";
import $ from "jquery";

// LP 회전각도 초기화 변수
let rotation = 0;

function handleplayer() {
  let audio = $("#music")[0];
  const play_btn = $(".play_song_btn");
  const elapsed = $("#elapsed");
  const total = $("#total_timer");
  const current = $("#progress_timer");
  const volumeBar = $("#volume");
  const lp = $(".artwork");

  // 오디오 재생/멈춤버튼 토글
  play_btn.click(function () {
    play_btn.toggleClass("active");
    /* active 클래스 가지고있으면 이미지 변경! */
    if (play_btn.hasClass("active") && audio.paused) {
      play_btn.find("img").attr("src", "./images/player/bx-pause.svg");
      audio.play();
      rotateLp();
    } else {
      play_btn.find("img").attr("src", "./images/player/bx-play-circle.svg");
      audio.pause();
      stopLp();
    }
  });

  // // 오디오 볼륨 조절
  // volumeBar.on("change", function() { // 볼륨 조절바를 조작할때 이벤트 발생!
  //   const volumeVal = $(this).val(); // 오디오 요소 - 현재 볼륨값을 값으로 가져옴
  //   console.log(volumeVal)
  //   audio.volume = volumeVal;
  // })

  function rotateLp() {
    // 회전각 1씩 증가!
    rotation += 1;
    // 1씩 증가된 회전각 만큼 회전
    lp.css("transform", "rotate(" + rotation + "deg)");
    requestAnimationFrame(rotateLp);
  }

  function stopLp() {
    // 회전각 1씩 감소!
    rotation -= 1;
    // 1씩 감소된 회전각 만큼 회전
    lp.css("transform", "rotate(" + rotation + "deg)");
    requestAnimationFrame(stopLp);
  }

  // 오디오 볼륨 조절
  // volumeBar.on("change", function() { // 볼륨 조절바를 조작할때 이벤트 발생!
  //   const volumeVal = $(this).val(); // 오디오 요소 - 현재 볼륨값을 값으로 가져옴
  //   console.log(volumeVal)
  //   // audio.volume = volumeVal;
  // })
} // handleplayer 함수

$(document).ready(function () {
  handleplayer();
});

// 플레이어 출력용 컴포넌트
function Player(props) {
  const sel_data = ban_data[props.cat];
  
  const [volume, setVolume] = useState(1);
  const [currentSongIndex, setCurrentSongIndex] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const audioPlayer = useRef(null);
  const soundRef = useRef(null);

  // const handleVolumeChange = (event) => {
  //   const volumeValue = event.target.value;

  //   // audioPlayer.current.volume = volumeValue;
  //   // setVolume(volumeValue);

  //   // console.log(audioPlayer.current.volume)
  //   const audioElement = audioPlayer.current;
  //   audioElement.volume = volumeValue;
  //   setVolume(volumeValue);
  // };

  // 다음 버튼 클릭시 다음 곡 재생 
  const playNextSong = () => {
    const nextIndex = (currentSongIndex + 1) % sel_data.length;
    setCurrentSongIndex(nextIndex);
    playAudio(sel_data[nextIndex].vsrc);
  };

  // Howl 라이브러리(이전/다음 곡 컨트롤)
  const playAudio = (audioSrc) => {
    if (soundRef.current) {
      soundRef.current.stop();
    }
    
    const sound = new Howl({
      src: [audioSrc],
      html5: true,
      onend: playNextSong,
    });
    sound.play();
    // soundRef.current = sound; // 현재 재생 중인 곡을 참조
    setIsPlaying(true);
  };

  const [isMuted, setIsMuted] = useState(true); // 초기 상태는 음소거(true)로 설정

  const playPreviousSong = () => {
    const previousIndex = currentSongIndex === 0 ? sel_data.length - 1 : currentSongIndex - 1;
    setCurrentSongIndex(previousIndex);
    playAudio(sel_data[previousIndex].vsrc);
  };

  // const handlePlayButtonClick = () => {
  //   setIsMuted(false); // 재생 버튼 클릭 시 음소거 해제
  // };
  const togglePlay = () => {
    if (isPlaying) {
      const sound = soundRef.current;
      if (sound) {
        sound.pause();
        setIsPlaying(false);
      }
    } else {
      playAudio(sel_data[currentSongIndex].vsrc);
    }
  };

  const handleNextButtonClick = () => {
    const sound = soundRef.current;
    if (sound) {
      sound.stop();
    }
    playNextSong();
  };

  const handlePreviousButtonClick = () => {
    const sound = soundRef.current;
    if (sound) {
      sound.stop();
    }
    playPreviousSong();
  };


  const x = ban_data.main[currentSongIndex];
  console.log(x)

  return (
    <div className={"player"}>
      <span id="arm"></span>
      <ul>
        <li className="artwork">
          <a href="#">
            <img src={x.isrc} alt="레코드이미지" />
          </a>
        </li>
        <li className="info">
          {/* 곡 정보 */}
          <h1 id="album">{x.mtit.replace(/-/g, "")}</h1>
          <h4 id="artist">(G)I-DLe</h4>
          <h2 id="song">{x.mtit}</h2>
          <div className="button-items">
            <audio id="music">
              <source src={x.vsrc} type="audio/mp3" />
            </audio>
            {/* 음반 리스트 */}
            <div id="slider">
              {/* 음원 재생시간 프로그레스바 */}
              <div id="elapsed"></div>
              <div id="buffered"></div>
            </div>
            {/* 현재 재생시간 */}
            <p id="progress_timer" className="timer">
              00:00
            </p>
            {/* 총 재생시간 */}
            <p id="total_timer" className="timer">
              00:00
            </p>
            <div className="controls">
            <span className={`prev_song_btn ${isPlaying ? "" : "disabled"}`} onClick={handlePreviousButtonClick}>
                <i className="bx bx-skip-previous"></i>
              </span>
              <span className="play_song_btn" onClick={togglePlay}>
                <img src={"./images/player/bx-play-circle.svg"} alt="정지버튼" />
              </span>
              <span className="next_song_btn" onClick={playNextSong}>
                <i className="bx bx-skip-next"></i>
              </span>
              <div className="slider">
                <div className="volume"></div>
                <input type="range" id="volume" min={0} max={100} step={1} value={volume} onChange={handleVolumeChange} />
              </div>
            </div>
          </div>
        </li>
      </ul>
    </div>
  );
} // Player 함수

export default Player;
