// 플레이어 컴포넌트 - Player.js
import React, { useRef, useState, useEffect } from "react";
// 오디오 재생/컨트롤 라이브러리
import { Howl } from "howler";
// 플레이어CSS
import "../scss/player.css";
// 메인함수
import { handleHover, handleTime, minutes2, seconds2, currentTime2 } from "../js/commonFn";
import $ from "jquery";
// 배너 데이터
import ban_data from "../data/ban";

// 플레이어 출력용 컴포넌트
function Player(props) {
  // LP 회전각도 초기화 변수
  let rotation = 0;

  // 전체곡순번
  const [songSeq, setSongSeq] = useState(0);

  let audio, play_btn, lp;

  const audioPlayer = useRef(null);
  const sel_data = ban_data[props.cat];
  const [volume, setVolume] = useState(1);

  function handleplayer() {
    audio = $("#music")[0];
    play_btn = $(".play_song_btn");
    lp = $(".artwork");
  } // handleplayer 함수

  $(document).ready(function () {
    // Player 함수
    handleplayer();
    handleTime();
    // CD Hover함수
    handleHover();
    // CD Wheel 함수
    // handleWheel();
  });

  // 볼륨조절 함수
  const volumeChange = (event) => {
    const volumeValue = event.target.value;
    audio.volume = volumeValue;
    setVolume(volumeValue);
  };

  const [currentSongIndex, setCurrentSongIndex] = useState(0);
  const soundRef = useRef(null);
  let rotsts = 1;

  // 다음 버튼 클릭시 다음 순번부터 곡 재생 (1~0)
  const currentPlayStop = (e) => {
    play_btn.toggleClass("active");
    // console.log("재생멈춤버튼!!!");
    /* active 클래스 가지고있으면 이미지 변경! */
    if (play_btn.hasClass("active")) {
      play_btn.find("img").attr("src", "./images/player/bx-pause.svg");
      audio.play();
      rotsts = 1;
      rotateLp();
    } else {
      play_btn.find("img").attr("src", "./images/player/bx-play-circle.svg");
      audio.pause();
      rotsts = 0;
    }
  };

  const rotateLp = () => {
    // console.log("상태값:",rotsts)
    // 회전각 1씩 증가!
    rotation += 1;
    // 1씩 증가된 회전각 만큼 회전
    lp.css("transform", "rotate(" + rotation + "deg)");
    if (rotsts && play_btn.hasClass("active")) setTimeout(rotateLp, 10);
  };

  // 다음 버튼 클릭시 다음 순번부터 곡 재생 (1~0)
  const playNextSong = () => {
    const nextIndex = (currentSongIndex + 1) % sel_data.length;
    setCurrentSongIndex(nextIndex);
    setSongSeq(nextIndex);

    if (play_btn.hasClass("active")) {
      setTimeout(() => audio.play(), 10);
      // changeSongTxt(nextIndex)
      handleTime(nextIndex);
    }
  };

  const playPrevSong = () => {
    const prevIndex = (currentSongIndex - 1 + sel_data.length) % sel_data.length;
    setCurrentSongIndex(prevIndex);
    const audioSrc = sel_data[prevIndex].vsrc;
    // console.log(prevIndex);
    setSongSeq(prevIndex);

    if (play_btn.hasClass("active")) {
      setTimeout(() => audio.play(), 10);
    }
    // console.log(audio);
  };

  // 재생시간에 따라 특정 값으로 텍스트 변경
  const changeSongTxt = (songSrc, currentTime2) => {
    // const timeHandler = handleTime();
    // const timeValues = timeHandler.getTimeValues();

    // console.log(timeValues.minutes2);
    console.log("현재시간");

    // console.log(songSrc)
    const songTxt = $("#song");
    // if(songSrc == 3){ // 3 - I love

    // function handleTime() {
      let audio = $("#music")[0];
      const elapsed = $("#elapsed");
      const total1 = $("#total_timer > .tot1");
      const total2 = $("#total_timer > .tot2");
      // console.log(total1,total2)
      const current = $("#progress_timer");

      // 오디오의 재생 시간이 업데이트될 때마다 함수 호출!
      audio.addEventListener("timeupdate", function () {
        // timeupdate - 오디오 또는 비디오의 재생 시간이 변경될 때마다 발생하는 이벤트
        // 오디오의 재생 시간과 전체 재생 시간 가져오기
        const currentTime = audio.currentTime;
        const duration = audio.duration || 0; // 오디오 총 길이
        // 음원 총 재생시간 구해서 시간 업데이트
        const minutes = Math.floor(duration / 60);
        const seconds = Math.floor(duration % 60);
        // let totalTime = minutes.toString().padStart(2,"0") + ":" + seconds.toString().padStart(2,"0"); // 자료형을 문자화한 후 앞에 "0"을 추가!
        let totalTime1 = minutes.toString().padStart(2, "0");
        let totalTime2 = seconds.toString().padStart(2, "0");
        total1.text(totalTime1);
        total2.text(totalTime2);

        // 음원 현재 재생시간 구해서 시간 업데이트
        const minutes2 = Math.floor(currentTime / 60);
        const seconds2 = Math.floor(currentTime % 60);
        const currentTime2 = minutes2.toString().padStart(2, "0") + ":" + seconds2.toString().padStart(2, "0"); // 자료형을 문자화한 후 앞에 "0"을 추가!
        current.text(currentTime2);
        console.log(currentTime2);

        // 프로그레스 바의 너비 계산
        const progress = (currentTime / duration) * 100 + "%";
        // console.log("프로그레스바너비:",progress)

        // 프로그레스 바 업데이트
        elapsed.css("width", progress);


        switch (
          songSrc // 3 - I love
        ) {
          case "3":
            return;
        }
      });
    // }
  };

  const srcList = ["./audios/i feel.mp3", "./audios/Expectations.mp3", "./audios/Nxde.mp3", "./audios/i love.mp3", "./audios/dumdi dumdi.mp3", "./audios/i am.mp3", "./audios/tomboy.mp3"];

  // main 배열의 각 순번 별로 데이터 접근 - currentSongIndex : 0, 1, 2, 3,..
  const x = ban_data.main[currentSongIndex];

  return (
    <div className={"player"}>
      <span id="arm"></span>
      <ul>
        <li className="artwork">
          <a href="#">
            <img src={x.isrc} alt="레코드이미지" />
          </a>
        </li>
        <li className="info">
          {/* 곡 정보 */}
          <h1 id="album">{x.mtit.replace(/-/g, "")}</h1>
          <h4 id="artist">(G)I-DLe</h4>
          <h2 id="song"></h2>
          <div className="button-items">
            <audio id="music" src={srcList[songSeq]}></audio>
            {/* 음반 리스트 */}
            <div id="slider">
              {/* 음원 재생시간 프로그레스바 */}
              <div id="elapsed"></div>
              <div id="buffered"></div>
            </div>
            {/* 현재 재생시간 */}
            <p id="progress_timer" className="timer">
              00:00
            </p>
            {/* 총 재생시간 */}
            <p id="total_timer" className="timer">
              <span className="tot1">00</span>:<span className="tot2">00</span>
            </p>
            <div className="controls">
              <span className="prev_song_btn" onClick={playPrevSong}>
                <i className="bx bx-skip-previous"></i>
              </span>
              <span className="play_song_btn" onClick={currentPlayStop}>
                <img src={"./images/player/bx-play-circle.svg"} alt="정지버튼" />
              </span>
              <span className="next_song_btn" onClick={playNextSong}>
                <i className="bx bx-skip-next"></i>
              </span>
              <div className="slider">
                <div className="volume"></div>
                <input type="range" id="volume" min={0} max={1} step={0.1} value={volume} onChange={volumeChange} />
              </div>
            </div>
          </div>
        </li>
      </ul>
    </div>
  );
} // Player 함수

export default Player;
