import { Link, Outlet } from "react-router-dom";
import "./scss/layout.css";

/****************************************** 
    함수명: handleToggle
    기능: 원형 네비 메뉴 클릭시 GNB 등장
******************************************/
function handleToggle() {
  const nav = document.querySelector(".circular_nav");
  const toggleBtn = nav.querySelector(".toggle_btn");

  nav.classList.toggle("active");
  toggleBtn.classList.toggle("active");
}

const Layout = () => {
  return (
    <>
    {/* 1. 메인영역 */}
      {/* 1-1. 우측 메인(원형 GNB 토글러) */}
      <nav className="circular_nav">
        <div className="nav_content">
          {/* 메뉴토글 버튼 */}
          <div className="toggle_btn" onClick={handleToggle}>
            <i className="bx bx-plus"></i>
          </div>
          {/* 1. 홈버튼 */}
          <span style={{"--i":1}}>
            <Link to="/">
              <i className="bx bxs-home"></i>
            </Link>
          </span>
          {/* 2. 갤러리버튼 */}
          <span style={{"--i":2}}>
            <Link to="/ga">
              <i className="bx bxs-camera"></i>
            </Link>
          </span>
          {/* 3. 비디오버튼 */}
          <span style={{"--i":3}}>
            <Link to="/vd">
              <i className="bx bxs-video"></i>
            </Link>
          </span>
          {/* 4. 앨범버튼 */}
          <span style={{"--i":4}}>
            <Link to="/al">
              <i className="bx bxs-music"></i>
            </Link>
          </span>
          {/* 5. 프로필버튼 */}
          <span>
            <Link to="/pf">
              <i className="bx bxs-user-rectangle"></i>
            </Link>
          </span>
        </div>
      </nav>
      {/* 1-2. 좌측 메인(배너) */}
      <Outlet/>
    </>
  );
};

export default Layout;
