// 제이쿼리
import $ from "jquery";
// 배너 데이터
import ban_data from "../data/ban";

export default function Video_Ban(){
    // 데이터 셋팅
    const vdata = ban_data;

    // 비디오보이기 함수
    const showVid = (src,tit) => {
        
        let ifr = $(".playvid iframe");
        // 1. 아이프레임 src넣기
        ifr.attr("src",src+"?autoplay=1");
        // 2. 비디오 타이틀넣기
        $(".ifrtit").text(tit);

        let vdbx = $(".vidbx");
        // 3. 비디오 전체박스 보이기
        vdbx.fadeIn(300);
        // 4. 닫기버튼 셋팅
        $(".cbtn").click(()=>{
            vdbx.fadeOut(300);
            ifr.attr("src","");
        })
    } // Showvid // 
}
