// 플레이어 컴포넌트 - Player.js
import React, { useState } from "react";
// 플레이어CSS
import "../scss/player.css";
// 배너 데이터
import ban_data from "../data/ban";
import $ from "jquery";

// 제이쿼리 로드구역 함수 /////////
function jqFn() {
    $(() => {}); //////// jQB ///////////
} ////////////// jQFn ///////////

/****************************************** 
    함수명: player
    기능: 
    (1) 슬라이드li 2번째 요소에 맞춰 플레이어 음원/커버 변경
    (2) 오디오 볼륨조절
    (3) 음원 현재 재생시간/ 총 재생시간 업데이트
    (4) 플레이버튼 토글하여 음원 재생/멈춤
******************************************/
function handlePlayer() {
    // const [isPlaying, setIsPlaying] = useState(false);
    // const [currentTime, setCurrentTime] = useState(0);
    // const [duration, setDuration] = useState(0);
    const [volume, setVolume] = useState(0);
  
    // const handlePlayPause = () => {
    //   setIsPlaying((prevIsPlaying) => !prevIsPlaying);
    // };
  
    // const handleTimeUpdate = (event) => {
    //   const audio = event.target;
    //   setCurrentTime(audio.currentTime);
    //   setDuration(audio.duration);
    // };
  
    const handleVolume = (event) => {
      const volumeValue = event.target.value;
      setVolume(volumeValue);
    };
}

// 함수호출!
handlePlayer();

// 플레이어 출력용 컴포넌트
function Player() {
  return (
    <>
      {ban_data.map((x, i) => (
        <div className="player" key={i}> 
          <span id="arm"></span>
          <ul>
            <li className="artwork">
              <a href="#">
                <img src={x.isrc} alt="레코드이미지" />
              </a>
            </li>
            <li className="info">
              {/* 곡 정보 */}
              <h1 id="album">{x.mtit}</h1>
              <h4 id="artist">(G)I-DLe</h4>
              <h2 id="song">{x.mtit}</h2>
              <div className="button-items">
                <audio id="music">
                  <source src={x.vsrc} type="audio/mp3" />
                </audio>
                {/* 음반 리스트 */}
                <div id="slider">
                  {/* 음원 재생시간 프로그레스바 */}
                  <div id="elapsed"></div>
                  <div id="buffered"></div>
                </div>
                {/* 현재 재생시간 */}
                <p id="progress_timer" className="timer">
                  00:00
                </p>
                {/* 총 재생시간 */}
                <p id="total_timer" className="timer">
                  00:00
                </p>
                <div className="controls">
                  <span className="prev_song_btn">
                    <i className="bx bx-skip-previous"></i>
                  </span>
                  <span className="play_song_btn">
                    <img src={"./images/player/bx-play-circle.svg"} alt="정지버튼" />
                  </span>
                  <span className="next_song_btn">
                    <i className="bx bx-skip-next"></i>
                  </span>
                  <div className="slider">
                    <div className="volume"></div>
                    <input type="range" id="volume" min="0" max="1" step="0.01" onChange={handleVolume}/>
                  </div>
                </div>
              </div>
            </li>
          </ul>
        </div>
      ))}
      {/* 빈루트를 만들고 JS로드함수포함 */}
      {jqFn()}
    </>
  );
}

export default Player;
